<?php namespace backend\controllers;use yii\web\Controller;use yii\data\ActiveDataProvider;use frontend\models\Form;use Yii;use yii\filters\AccessControl;use yii\filters\VerbFilter;class FormController extends FilterRoleController{    public function actionIndex()	{		$query = Form::find();        $dataProvider = new ActiveDataProvider([            'query' => $query,        ]);        return $this->render('index');	}  public function actionDelete($id)  {        $this->findModel($id)->delete();        return $this->redirect(['index']);  }     protected function findModel($id)    {        if (($model = Form::findOne(['id' => $id])) !== null) {            return $model;        }        throw new NotFoundHttpException(Yii::t('app', 'The requested page does not exist.'));    }}?>