<?phpnamespace frontend\controllers;use common\models\Krossword;use yii\web\Controller;use yii\data\Pagination;use yii\web\NotFoundHttpException;class KrosswordController extends FilterController{    public function actionIndex()    {        $query = Krossword::find();        $count = $query->count();        $pagination = new Pagination([            'totalCount' => $count,            'pageSize' => 6        ]);        $model = $query->limit($pagination->limit)->offset($pagination->offset)->where(['status' => 1])->all();        return $this->render('index', [            'model' => $model,            'pagination' => $pagination,        ]);    }    public function actionDetail(int $id)    {        $model = Krossword::findOne($id);        if ($model) {            $model->updateCounters(['views_count' => 1]);            return $this->render('detail',[                'model' => $model            ]);        }        else {            throw new NotFoundHttpException('No such information exists!');        }    }}?>