<?phpnamespace frontend\models;use common\models\User;use yii\base\InvalidArgumentException;use yii\base\Model;class VerifyEmailForm extends Model{    /**     * @var string     */    public $token;    /**     * @var User     */    private $_user;    /**     * Creates a form model with given token.     *     * @param string $token     * @param array $config name-value pairs that will be used to initialize the object properties     * @throws InvalidArgumentException if token is empty or not valid     */    public function __construct($token, array $config = [])    {        if (empty($token) || !is_string($token)) {            throw new InvalidArgumentException('Tasdiqlash elektron pochta belgisi boʻsh boʻlmasligi kerak.');        }        $this->_user = User::findByVerificationToken($token);        if (!$this->_user) {            throw new InvalidArgumentException('Tasdiqlash elektron pochta belgisi noto‘g‘ri.');        }        parent::__construct($config);    }    public function verifyEmail()    {        $user = $this->_user;        $user->status = User::STATUS_ACTIVE;        return $user->save(false) ? $user : null;    }}